function* numberGenerator(init = 0, increment = 1, maxIterations = Infinity) {
let current = init;
let iterations = 0;
while (iterations < maxIterations) {
yield current;
current += increment;
iterations++; } }

function* filter(generator, predicate) {
for (const num of generator) {
if (predicate(num)) {
yield num;
}
} }

function* cut(generator, limit) {
let count = 0;
for (const num of generator) {
if (count >= limit) { return; }
yield num;
count++;
} }

function map(generator, f) {
const mappedArray = [];
for (const num of generator) {
mappedArray.push(f(num));
}
return mappedArray; }


const nats = numberGenerator(0, 2);
const even = filter(nats, (n) => n % 2 === 0);
const evenLessThanEleven = filter(even, (n) => n < 11);
const cutNumbers = cut(evenLessThanEleven, 5);
const result = map(cutNumbers, (n) => n);
console.log(result);
